def this_function(s: str) -> bool:
    if '-' not in s:
        return True
    first_part = s.split('-', 1)[0]
    if not first_part.isdigit():
        return False
    num = int(first_part)
    return 0 <= num <= 2025

def this_function(s: str) -> bool:
    parts = s.split('-')
    if len(parts) - 1 < 2:
        return True
    middle = parts[1]
    if not middle.isdigit():
        return False
    num = int(middle)
    return 1 <= num <= 12

def this_function(s: str) -> bool:
    if '-' not in s:
