def this_function(s: str) -> bool: 
    ''' Check if the email shall not contain a space character.'''
    return ' ' not in s

def this_function(s: str) -> bool:
    ''' Check if the email does not start with a @ character.'''
    return not s.startswith('@')

def this_function(s: str) -> bool: 
    ''' Check if the email has exactly one @ character.'''
    return s.count('@') == 1
